exports.client_to_domainname=(function(buf){
	var buf_len=buf.length;
	var ii=12;//在12的位置可以读取到第一位域名的长度
	var domain_str='';
	for(var j=1;j<=10;j++){
		var domain_alen=buf[ii];//得知第一位域名的长度
		if(domain_alen==0){break;}
		var domain_astr='';
		for(var i=1;i<=domain_alen;i++){//域名多长就循环几次
			ii++;
			domain_astr+=String.fromCharCode(buf[ii]);
		}
		ii++;
		domain_str+=domain_astr+'.';//把这次取到的域名位加上去
	}
	return(domain_str);
});
