module.exports.date=require('./date').date;
module.exports.gbk_to_utf8=require('./gbk_to_utf8').gbk_to_utf8;
module.exports.log=(function(str){
	console.log(module.exports.date('H:i:s.K')+' '+str);
});
module.exports.log1=(function(str){//开始
	process.stdout.write(module.exports.date('H:i:s.K')+' '+str);
});
module.exports.log2=(function(str){//中间
	process.stdout.write(str);
});
module.exports.log3=(function(str){//结束
	console.log(str);
});
module.exports.log4=(function(str){//执行过程需要报错
	console.log("\r\n"+str);
});
module.exports.http=require('./http');
module.exports.dns=require('./dns');
module.exports.base64=require('./base64');
module.exports.intval=(function(mixed_var, base){
    var tmp;
    var type = typeof(mixed_var);
     if (type === 'boolean') {
        return +mixed_var;
    } else if (type === 'string') {
        tmp = parseInt(mixed_var, base || 10);
        return (isNaN(tmp) || !isFinite(tmp)) ? 0 : tmp;    } else if (type === 'number' && isFinite(mixed_var)) {
        return mixed_var | 0;
    } else {
        return 0;
    }
});
module.exports.md5=require('./md5').md5;
